import "./App.css";
import react,{ useState } from "react";

function App() {
  const[count,setCount] = useState(0)
  function decreaseHandler(){
    // count= count-1;
    setCount(count-1)
  }

  function increaseHandeler(){
    // count= count+1;
    setCount(count+1);
  }
  function resetHandeler(){
    setCount(0);
  }
  return (
   <div className="w-[100vw] h-[100vh] flex justify-center items-center bg-[#344151] flex-col gap-10">
    <div className="text-[#0398d4] text-2xl">Increment and Decrement</div>

    <div className="bg-white flex justify-center gap-12 py-3 rounded-sm text-[25px] text-[#344151]">
    <button onClick={decreaseHandler} className="border-r-2 w-20 border-[#bfbfbf] text-5xl">-</button>
    <div>{count}</div>
    <button onClick={increaseHandeler}  className="border-r-2 w-20 border-[#bfbfbf] text-5xl">+</button>
    </div>

    <div>
      <button  onClick={resetHandeler}>Reset</button>
    </div>

   </div>

  );
}

export default App;
